import { PlayerColor, PieceType } from "../../utils/Constants";
import { PieceModel } from "./PieceModel";
import { SquareModel } from "../SquareModel";
import { ChessBoardModel } from "../ChessBoardModel";

export class RookModel extends PieceModel{
    public constructor(type: PieceType, color: PlayerColor){
        super(type, color);
    }

    public validMove(boardModel:ChessBoardModel, startSquare:SquareModel,
        endSquare:SquareModel, playerColor:PlayerColor): boolean {
        return true;
    }

    private updatePossibleMoves(boardModel:ChessBoardModel, square:SquareModel, playerColor:PlayerColor):Set<string>{
        const rook = square.getPiece();
        const board = boardModel.getChessBoard();
        this.possibleMoves.clear();

        if(rook){
            const rookDirections = this.rookDirections();
            const left = rookDirections.left;
            const right = rookDirections.right;
            const up = rookDirections.up;
            const down = rookDirections.down;

            const chessNotation = square.getPos();
            const posArray = boardModel.posToArrayPos(chessNotation);

            if(posArray){
                let flag = true;
                let i = posArray.i;
                let j = posArray.j; 

                this.checkSquare(board,i,j,left.dx,left.dy,playerColor);
                this.checkSquare(board,i,j,right.dx,right.dy,playerColor);
                this.checkSquare(board,i,j,up.dx,up.dy,playerColor);
                this.checkSquare(board,i,j,down.dx,down.dy,playerColor);

                // while(flag){
                //     let leftI:number = i+left.dy;
                //     let leftJ:number = j+left.dx;
                //     if(this.withinBoard(leftI,leftJ)){
                //         const tempSquare:SquareModel = board[leftI][leftJ];
                //         if((tempSquare.getPiece() && tempSquare.getPiece()?.getColor() !== playerColor)
                //             || !tempSquare.getPiece()){
                //             this.possibleMoves.add(tempSquare.getPos());
                //             i = leftI;
                //             j = leftJ;
                //             flag = true;
                //         }
                //         else{ flag = false }
                //     }
                // }
                // while(flag){
                //     let rightI:number = i+right.dy;
                //     let rightJ:number = j+right.dx;
                //     if(this.withinBoard(rightI,rightJ)){
                //         const tempSquare:SquareModel = board[rightI][rightJ];
                //         if((tempSquare.getPiece() && tempSquare.getPiece()?.getColor() !== playerColor)
                //             || !tempSquare.getPiece()){
                //             this.possibleMoves.add(tempSquare.getPos());
                //             i = rightI;
                //             j = rightJ;
                //             flag = true;
                //         }
                //         else{ flag = false }
                //     }


                // }
            }
        }
        return new Set<string>();
    }
    private checkSquare(board:Array<Array<SquareModel>>,i:number,j:number,dx:number,
                        dy:number, playerColor:PlayerColor){
        let flag = true;
        while(flag){
            let dI:number = i+dy;
            let dJ:number = j+dx;
            if(this.withinBoard(dI,dJ)){
                const tempSquare:SquareModel = board[dI][dJ];
                if((tempSquare.getPiece() && tempSquare.getPiece()?.getColor() !== playerColor)
                    || !tempSquare.getPiece()){
                    this.possibleMoves.add(tempSquare.getPos());
                    i = dI;
                    j = dJ;
                    flag = true;
                }
                else{ flag = false }
            }
        }
    }

    private rookDirections(){
        return {
            left:{
                dx:-1,
                dy:0
            },
            right:{
                dx:1,
                dy:0
            },
            up:{
                dx:0,
                dy:-1
            },
            down:{
                dx:0,
                dy:1
            }
        }
    }
}
