import { BOARD_SIZE } from "../Constants";
import { ChessBoardModel } from "../model/ChessBoardModel";
import { SquareModel } from "../model/SquareModel";

export function findModelSquareFromView(squareView: HTMLElement, boardModel:ChessBoardModel){

    console.log(boardModel);

    const squarePos = squareView.id;
    const chessBoard:Array<Array<SquareModel>> = boardModel.getChessBoard();
    if(squareView){
        for(let i = 0; i< BOARD_SIZE; i++){
            for(let j = 0; j<BOARD_SIZE; j++){
                if(chessBoard[i][j].getPos() === squarePos){
                    return chessBoard[i][j];
                }
            }
        }
    }
    return undefined;
}
