import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";

export default function Dashboard() {
  const navigate = useNavigate();

  const { user } = useSelector((state:any) => state.auth);

  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [selectedColor, setSelectedColor] = useState(""); // "black" or "white"
  const [username, setUsername] = useState("");

  const handleCreateGameClick = () => {
    setShowCreateModal(true);
  };

  const handleJoinGameClick = () => {
    setShowJoinModal(true);
  };

  const handleColorSelect = (color:string) => {
    setSelectedColor(color);
    setShowCreateModal(false);
  };

  const handleJoinSubmit = () => {
    // Handle joining the game with the selected username
    setShowJoinModal(false);
  };

  return (
    <div>
      <h1>Welcome {user ? user.name : 'Guest'}</h1>
      <button className="btn" onClick={handleCreateGameClick}>
        Create Game
      </button>
      <button className="btn" onClick={handleJoinGameClick}>
        Join Game
      </button>

      {showCreateModal && (
        <div className="modal">
          <h2>Select Color</h2>
          <button onClick={() => handleColorSelect("black")}>Black</button>
          <button onClick={() => handleColorSelect("white")}>White</button>
        </div>
      )}

      {showJoinModal && (
        <div className="modal">
          <h2>Join Game</h2>
          <input
            type="text"
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleJoinSubmit}>Join</button>
        </div>
      )}
    </div>
  );
}
