          const startSquare = activePiece.parentElement;
          const endSquare: HTMLElement | null = element;

          const activePieceColor: string = activePiece.id.includes("White")
            ? PlayerColor.WHITE
            : PlayerColor.BLACK;

          if (endSquare && endSquare.classList.contains("piece")) {
            const endPieceColor = endSquare.id.includes("White")
              ? PlayerColor.WHITE
              : PlayerColor.BLACK;

            if (endPieceColor === activePieceColor) {
              setActivePiece(endSquare);
              return;
            }
          }

          if (startSquare && endSquare && startSquare !== endSquare) {
            const updatedBoardModel = boardModel.clone();
            const updatedBoard = updatedBoardModel.getChessBoard();
            const startPos = findModelSquareFromView(startSquare, updatedBoardModel);
            const endPos = findModelSquareFromView(endSquare, updatedBoardModel);

            if (startPos && endPos) {
              updatedBoardModel.pieceMove(
                updatedBoard[startPos.i][startPos.j],
                updatedBoard[endPos.i][endPos.j]
              );
            }

            setBoardModel(updatedBoardModel);

            if (startSquare.contains(activePiece)) {
              startSquare.removeChild(activePiece);
