import { API_URL,PlayerColor } from "../utils/Constants";
import Chessboard from "../components/chess/game/board/Chessboard";
import './GamePlayPage.css';
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"
import { useSelector, useDispatch } from "react-redux"
//import { setSocket, resetSocket } from "../lib/webSocket/socketSlice";
import io from "socket.io-client";

export default function GameplayPage(){

    const navigate = useNavigate();
    const dispatch = useDispatch();

    const {user} = useSelector((state:any) => state.auth);
    //const socket = useSelector((state:any) =>state.socket)

    useEffect(() =>{
        if(!user){
            navigate('/login')
        }
    },[user,navigate]);

    useEffect(() => {
        //if (!socket) {
            const socket = io("http://localhost:5000");
            let game = localStorage.getItem('game');
            if(!game) navigate('/');
            else{
                game = JSON.parse(game);
                console.log(game);
            }
            //dispatch(setSocket(newSocket));
        //}
        // return () => {
        //     dispatch(resetSocket());
        // };
    }, []);

    const [playerColor, setPlayerColor] = useState(PlayerColor.WHITE);
    const [loaded, setLoaded] = useState(false);
    useEffect(()=>{
        fetch(API_URL+"/api/game/create")
        .then(res => res.json())
        .then(data => {
            setPlayerColor(data.color);
            setLoaded(true);
        });
    },[]);

    return (
        <div id="gamePlayPage">
            {loaded && <Chessboard playerColor={playerColor}></Chessboard>}
        </div>
    );
}
