import { PawnModel } from "./PawnModel";
import { PieceModel } from "./PieceModel";
import { SquareModel } from "./SquareModel";

export class ChessBoardModel{
    private chessBoard: SquareModel[][];
    private playerColor:string;

    public constructor(playerColor:string){
        this.playerColor = playerColor;

        let col = playerColor =="white"?
        ['a','b','c','d','e','f','g','h']:['h','g','f','e','d','c','b','a'];

        let row = playerColor == "white"?
        [8,7,6,5,4,3,2,1]:[1,2,3,4,5,6,7,8];

        this.chessBoard = [[],[],[],[],[],[],[],[]];
        for(let i = 0; i<8; i++){
            for(let j = 0; j<8; j++){
                let pos:string = col[i]+row[j];
                let color = (j+i+2) % 2 == 0? "white":"black"
                let piece:PieceModel;
                if(col)
                this.chessBoard[i].push(
                    new SquareModel(color,pos,new PawnModel("Pawn","white"))
                );
            }
        }
    }

    public genChessBoard():SquareModel[][]{
        return this.chessBoard;
    }
}
