import { API_URL,PlayerColor } from "../utils/Constants";
import Chessboard from "../components/chess/game/board/Chessboard";
import './GamePlayPage.css';
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"
import { useSelector, useDispatch } from "react-redux"
import io from "socket.io-client";
import axios from "axios";

export default function GameplayPage(){

    const navigate = useNavigate();
    const dispatch = useDispatch();

    const {user} = useSelector((state:any) => state.auth);
    const [socket, setSocket] = useState(io("http://localhost:5000"));
    const [playerColor, setPlayerColor] = useState(PlayerColor.WHITE);
    const [loaded, setLoaded] = useState(false);
    useEffect(() =>{
        if(!user){
            navigate('/login')
        }
    },[user,navigate]);

    useEffect(() => {
            //const socket = io("http://localhost:5000");
            const gameString = localStorage.getItem('game');
            if(!gameString) navigate('/');
            else{
                const game = JSON.parse(gameString);
                if(game.username) handleCreateGame(game.username);
                else handleJoinGame(game);
            }
    }, []);


    // useEffect(()=>{
    //     fetch(API_URL+"/api/game/create")
    //     .then(res => res.json())
    //     .then(data => {
    //         setPlayerColor(data.color);
    //         setLoaded(true);
    //     });
    // },[]);

    const handleCreateGame = (username:string)=>{
        axios.post(API_URL+"/api/game/create",{username:username})
        .then(res => {
            socket.emit('joinGame',username);
            setLoaded(true)
        })
        .catch(err => console.log(err));
    }
    const handleJoinGame = (username:string)=>{
        axios.post(API_URL+"/api/game/join",{username:username})
        .then(res => {
            socket.emit('joinGame',username);
            setLoaded(true)
        })
        .catch(err => console.log(err));
    }
    return (
        <div id="gamePlayPage">
            {loaded && <Chessboard playerColor={socket,playerColor}></Chessboard>}
        </div>
    );
}
