import react from 'react';
import './Chessboard.css';
import './Square';
import Square from './Square';

export default function Chessboard(props:{playerColor:string}){
    const playerColor = props.playerColor;

    let row = playerColor =="white"?
    ['a','b','c','d','e','f','g','h']:['h','g','f','e','d','c','b','a'];

    let col = playerColor == "white"?
    [8,7,6,5,4,3,2,1]:[1,2,3,4,5,6,7,8];

    let board:any = [[],[],[],[],[],[],[],[],[]];
    for(let i = 0; i<8; i++){
        for(let j = 0; j<8; j++){
            let pos:string = row[j]+col[i];
            let color = (j+i+2) % 2 == 0? "white":"black"
            board[i].push(
                <Square color={color} pos={pos}></Square>
            )
        }
    }
    console.log(board);
    return(
        <div id = "chessboard">
            {board}
        </div>
    );
}
