import { PlayerColor } from "../utils/Constants";
import Chessboard from "../components/chess/game/board/Chessboard";
import './GamePlayPage.css';
import { useEffect, useState } from "react";

export default function GameplayPage(){

    const [playerColor, setPlayerColor] = useState("");
    useEffect(()=>{
        fetch("http://localhost:5000/game")
        .then(res => res.json())
        .then(data => setPlayerColor(data.color));
    },[]);

    return (
        <div id="gamePlayPage">
            <Chessboard playerColor={playerColor}></Chessboard>
        </div>
    );
}
