import { PawnModel } from "./PawnModel";
import { PieceModel } from "./PieceModel";
import { RookModel } from "./RookModel";
import { SquareModel } from "./SquareModel";

export class ChessBoardModel{
    private chessBoard: Array<Array<SquareModel>>;
    private playerColor:string;

    public constructor(playerColor:string){
        this.playerColor = playerColor;

        let col = playerColor =="white"?
        ['a','b','c','d','e','f','g','h']:['h','g','f','e','d','c','b','a'];

        let row = playerColor == "white"?
        [8,7,6,5,4,3,2,1]:[1,2,3,4,5,6,7,8];

        this.chessBoard = [[],[],[],[],[],[],[],[]];
        for(let i = 0; i<8; i++){
            for(let j = 0; j<8; j++){

                let pos:string = col[j]+row[i];
                let color = (j+i+2) % 2 == 0? "white":"black"
                let piece:PieceModel | undefined = this.genPiece(col[i],row[j]);

                if(piece){
                    this.chessBoard[i].push(
                        new SquareModel(color,pos,piece)
                    );
                }else{
                    this.chessBoard[i].push(
                        new SquareModel(color,pos)
                    );
                }
            }
        }
        console.log(this.chessBoard);
    }

    public genChessBoard():Array<Array<SquareModel>>{
        return this.chessBoard;
    }
    private genPiece(col:string,row:number): PieceModel | undefined{
        if(row == 2){
            return new PawnModel("Pawn","white");
        }
        else if(row == 7){
            return new PawnModel("Pawn","black");
        }
        else if(row == 1){
            if(col == 'a' || col == 'h'){
                return new RookModel("Rook","white");
            }
        }
        else if(row == 8){
            if(col == 'a' || col == 'h'){
                return new RookModel("Rook","black");
            }
        }
        else{
            return undefined;
        }
    }
}
