import react from 'react';
import './Chessboard.css';
import './Square';
import Square from './Square';

export default function Chessboard(props:{playerColor:string}){
    let row = ['a','b','c','d','e','f','g','h'];
    let col = [1,2,3,4,5,6,7,8];
    const playerColor = props.playerColor;
    const startRow:string = playerColor === "white"?  'a':'h';
    const startCol:number = playerColor === "white"?  8:1;
    const endRow:string = playerColor === "white"? 'h':'a';
    const endCol:number = playerColor === "white"? 1:8;
    let board = [];
    for(let i = 0; i<col.length; i++){
        for(let j = 0; i<row.length; j++){
            let pos = row[j]+col[i];
            board.push(
                <Square color='white' pos={pos}></Square>
            )
        }
    }
    return(
        <div id = "chessboard">
            {board}
        </div>
    );
}
